// src/components/AddTruckForm.jsx
import React, { useState } from 'react';

const AddTruckForm = ({ onAdd, onCancel }) => {
  const [formData, setFormData] = useState({
    plateNumber: '',
    model: '',
    capacity: '',
    location: '',
    status: 'active'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd(formData);
    setFormData({
      plateNumber: '',
      model: '',
      capacity: '',
      location: '',
      status: 'active'
    });
  };

  return (
    <form className="add-truck-form" onSubmit={handleSubmit}>
      <h3>Add New Truck</h3>
      <label>Plate Number</label>
      <input name="plateNumber" value={formData.plateNumber} onChange={handleChange} required />

      <label>Model</label>
      <input name="model" value={formData.model} onChange={handleChange} required />

      <label>Capacity (Liters)</label>
      <input name="capacity" value={formData.capacity} onChange={handleChange} required />

      <label>Location</label>
      <input name="location" value={formData.location} onChange={handleChange} required />

      <label>Status</label>
      <select name="status" value={formData.status} onChange={handleChange}>
        <option value="active">Active</option>
        <option value="inactive">Inactive</option>
      </select>

      <div className="form-buttons">
        <button type="submit">Add Truck</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
    </form>
  );
};

export default AddTruckForm;
