import React, { useState } from 'react';
import AddTruckForm from './AddTruck';
import './Fleetmanagement.css';
import AddTruck from './CRUD/AddTruck';

const FleetManagement = () => {
  const [fleet, setFleet] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const handleAddTruck = (newTruck) => {
    setFleet((prev) => [...prev, newTruck]);
    setShowForm(false);
  };

  return (
    <div className="fleet-container">
      <h2>Fleet Management</h2>
      <button onClick={() => setShowForm(true)}>Add Truck</button>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal-box">
            <AddTruck
              onAdd={handleAddTruck}
              onClose={() => setShowForm(false)}
            />
          </div>
        </div>
      )}

      <ul className="fleet-list">
        {fleet.map((truck, index) => (
          <li key={index}>
            {truck.plateNumber} - {truck.model} - {truck.capacity}L - {truck.location} ({truck.status})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FleetManagement;
