import React, { useState } from 'react';
import Addcity from './Add/Addcity';
import Editcity from './Edit/EditCity';

export default function CityOperations() {
  const [cities, setCities] = useState([]);
  const [editingCity, setEditingCity] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);

  const handleAddCity = (newCity) => {
    setCities(prev => [...prev, newCity]);
    setShowAddForm(false);
  };

  const handleUpdateCity = (updatedCity) => {
    setCities(prev =>
      prev.map(city => city.name === updatedCity.name ? updatedCity : city)
    );
    setEditingCity(null);
  };

  const handleEditClick = (city) => {
    setEditingCity(city);
  };

  const handleCancel = () => {
    setShowAddForm(false);
    setEditingCity(null);
  };

  return (
    <div className="city-operations-content">
      <button onClick={() => setShowAddForm(true)}>Add New City</button>

      {showAddForm && (
        <Addcity onAddCity={handleAddCity} onCancel={handleCancel} />
      )}

      {editingCity && (
        <Editcity cityData={editingCity} onUpdateCity={handleUpdateCity} onCancel={handleCancel} />
      )}

      <div className="city-list">
        {cities.map((city, index) => (
          <div key={index} className="city-card">
            <h3>{city.name}</h3>
            <p>Manager: {city.manager}</p>
            <p>Pump: {city.pump}</p>
            <p>Status: {city.status}</p>
            <p>Fuel Left: {city.fuelLeft}</p>
            <button onClick={() => handleEditClick(city)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
}
