import React, { useState } from 'react';
import AddUserModal from './CRUD2/Add/Adduser';
import Sidebar from '../../../Component/SideBar/Sidebar';
import './Usermanagement.css';

export default function UserManagement() {
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([
    { name: 'John Admin', email: 'john@admin.com', initials: 'JA', LIC: '123456', MobNumber: '9876543210' },
    { name: 'Mike Wilson', email: 'mike@tanker.com', initials: 'MW', LIC: '654321', MobNumber: '9123456780' },
  ]);

  const handleAddUser = (newUserData) => {
    const initials = newUserData.name
      .split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase();

    const newUser = {
      ...newUserData,
      initials,
    };

    setUsers(prev => [...prev, newUser]);
    setShowModal(false);
  };

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="user-management-container">
      <Sidebar />
      <div className="main-content">
        <div className="header">
          <h2>User Management</h2>
          <button className="add-user-btn" onClick={() => setShowModal(true)}>+ Add New User</button>
        </div>

        <div className="filters">
          <input
            type="text"
            placeholder="Search users by name or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <table className="user-table">
          <thead>
            <tr>
              <th>User</th>
              <th>LIC</th>
              <th>Mobile</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user, index) => (
              <tr key={index}>
                <td>
                  <div className="user-info">
                    <div className="avatar">{user.initials}</div>
                    <div>
                      <strong>{user.name}</strong><br />
                      <span className="email">{user.email}</span>
                    </div>
                  </div>
                </td>
                <td>{user.LIC}</td>
                <td>{user.MobNumber}</td>
                <td>
                  <button className="edit">✏️ Edit</button>
                  <button className="delete">🗑️ Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showModal && (
          <AddUserModal
            onClose={() => setShowModal(false)}
            onSubmit={handleAddUser}
          />
        )}
      </div>
    </div>
  );
}
