// src/components/AddTruckForm.jsx
import React, { useState } from 'react';

const AddTruckForm = ({ onAdd, onCancel }) => {
  const [formData, setFormData] = useState({
    id: '',
    model: '',
    location: '',
    nextService: '',
    capacity: '',
    status: 'active'
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd(formData);
    onCancel();
  };

  return (
    <form className="add-truck-form" onSubmit={handleSubmit}>
      <h3>Add New Truck</h3>
      <input name="id" placeholder="Truck ID" onChange={handleChange} required />
      <input name="model" placeholder="Model" onChange={handleChange} required />
      <input name="location" placeholder="Location" onChange={handleChange} required />
      <input name="nextService" placeholder="Next Service Date" onChange={handleChange} required />
      <input name="capacity" placeholder="Capacity (L)" onChange={handleChange} required />
      <select name="status" onChange={handleChange}>
        <option value="active">Active</option>
        <option value="maintenance">Maintenance</option>
      </select>
      <div className="form-buttons">
        <button type="submit">Add Truck</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </div>
    </form>
  );
};

export default AddTruckForm;
