import React, { useState } from 'react';

import AddUserModal from './CRUD2/Add/Adduser';
 import SideBar from '../../../Component/SideBar/Sidebar'



export default function UserManagement() {
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('All Roles');

  // ✅ Add this function here
  const handleAddUser = (newUserData) => {
    console.log('New User Data:', newUserData);
    setShowModal(false); // Close modal after submission
  };

  const users = [
    { name: 'John Admin', email: 'john@admin.com', initials: 'JA'  },
    { name: 'Mike Wilson', email: 'mike@tanker.com', initials: 'MW' },
    { name: 'Sarah Johnson', email: 'sarah@tanker.com', initials: 'SJ'},
    { name: 'Robert Brown', email: 'robert@tanker.com', initials: 'RB'},
  ];

  

  return (
    <div className="user-management-container">
      <Sidebar />
      <div className="main-content">
        <div className="header">
          <h2>User Management</h2>
          <button className="add-user-btn" onClick={() => setShowModal(true)}>+ Add New User</button>
        </div>

        <div className="filters">
          <input
            type="text"
            placeholder="Search users by name or email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
       
        </div>

        <table className="user-table">
          <thead>
            <tr>
              <th>User</th>
              <th>Last Login</th>
              <th>Created</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map((user, index) => (
              <tr key={index}>
                <td>
                  <div className="user-info">
                    <div className="avatar">{user.initials}</div>
                    <div>
                      <strong>{user.name}</strong><br />
                      <span className="email">{user.email}</span>
                    </div>
                  </div>
                </td>
                <td>{user.login}</td>
                <td>{user.created}</td>
                <td>
                  <button className="edit">✏️</button>
                  <button className="delete">🗑️</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* ✅ Pass handleAddUser to the modal */}
       {showModal && (
  <AddUserModal
    onClose={() => setShowModal(false)}
    onSubmit={handleAddUser}
  />
)}

      </div>
    </div>
  );
}
