import React, { useState } from 'react';
import './Addcity.css';

export default function AddCityForm({ onAddCity, onCancel }) {
  const [formData, setFormData] = useState({
    name: '',
    manager: '',
    status: 'active',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddCity({ ...formData, pump: 'N/A', fuelLeft: '0 L' });
  };

  return (
    <div className="modal-form">
      <h2>Add New City Operation</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Enter city name" value={formData.name} onChange={handleChange} required />
        <input type="text" name="manager" placeholder="Enter manager name" value={formData.manager} onChange={handleChange} required />
        <select name="status" value={formData.status} onChange={handleChange}>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>
        <div className="form-buttons">
          <button type="submit" className="submit-btn">Add City</button>
          <button type="button" className="cancel-btn" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
}
