// src/pages/FleetManagement.jsx
import React, { useState } from 'react';
// import TruckCard from '../components/TruckCard';
import AddTruckForm from '../CRUD/AddTruck';

const initialTrucks = [
  {
    id: 'TRK-001',
    model: 'Volvo FH16',
    location: 'Mumbai Terminal',
    nextService: '15/4/2024',
    capacity: '25,000 L',
    status: 'active'
  },
  {
    id: 'TRK-002',
    model: 'Scania R Series',
    location: 'Delhi Depot',
    nextService: '10/4/2024',
    capacity: '30,000 L',
    status: 'maintenance'
  },
  {
    id: 'TRK-003',
    model: 'Mercedes Actros',
    location: 'Bangalore Hub',
    nextService: '20/4/2024',
    capacity: '28,000 L',
    status: 'active'
  }
];

const FleetManagement = () => {
  const [trucks, setTrucks] = useState(initialTrucks);
  const [showForm, setShowForm] = useState(false);

  const handleAddTruck = (newTruck) => {
    setTrucks([...trucks, newTruck]);
  };

  return (
    <div className="fleet-management">
      <header>
        <h2>Fleet Management</h2>
        <input type="text" placeholder="Search trucks..." />
        <select>
          <option>All Status</option>
          <option>Active</option>
          <option>Maintenance</option>
        </select>
        <button onClick={() => setShowForm(true)}>Add New Truck</button>
      </header>

      {showForm && (
        <AddTruckForm
          onAdd={handleAddTruck}
          onCancel={() => setShowForm(false)}
        />
      )}

      <div className="truck-list">
        {trucks.map((truck) => (
          <TruckCard key={truck.id} truck={truck} />
        ))}
      </div>
    </div>
  );
};

export default FleetManagement;
