import React, { useState } from 'react';
import Sidebar from '../../../Component/SideBar/Sidebar';
import { FaUserTie, FaGasPump, FaCheckCircle, FaTimesCircle, FaPlus, FaEdit, FaTrash } from 'react-icons/fa';
import './Cityoperation.css';

export default function Cityoperation() {
  const [searchTerm, setSearchTerm] = useState('');
  const [cities, setCities] = useState([
    {
      name: 'Mumbai',
      manager: 'Rajesh Kumar',
      pump: 'HP Petrol Pump - Andheri',
      status: 'active',
      fuelLeft: '12,500 L',
    },
    {
      name: 'Delhi',
      manager: 'Anita Sharma',
      pump: 'Indian Oil - Connaught Place',
      status: 'inactive',
      fuelLeft: '0 L',
    },
    {
      name: 'Bangalore',
      manager: 'Suresh Nair',
      pump: 'Bharat Petroleum - Koramangala',
      status: 'active',
      fuelLeft: '8,200 L',
    },
  ]);

  const filteredCities = cities.filter(city =>
    city.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = (name) => {
    const confirm = window.confirm(`Delete ${name}?`);
    if (confirm) {
      setCities(prev => prev.filter(city => city.name !== name));
    }
  };

  return (
    <div className="city-operations-container">
      <Sidebar />
      <div className="city-operations-content">
        <div className="header-row">
          <h1>City Operations</h1>
          <button className="add-city-btn">
            <FaPlus /> Add City
          </button>
        </div>

        <div className="search-bar">
          <input
            type="text"
            placeholder="Search city by name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="city-cards">
          {filteredCities.map(({ name, manager, pump, status, fuelLeft }) => (
            <div className="city-card" key={name}>
              <h2>{name}</h2>
              <p><FaUserTie /> Manager: {manager}</p>
              <p><FaGasPump /> Pump: {pump}</p>
              <p className={`status ${status}`}>
                {status === 'active' ? <FaCheckCircle /> : <FaTimesCircle />}
                {status.charAt(0).toUpperCase() + status.slice(1)}
              </p>
              <div className="fuel-info">
                <p>Remaining Fuel</p>
                <h3>{fuelLeft}</h3>
              </div>
              <div className="card-actions">
                <button className="edit-btn"><FaEdit /> Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(name)}><FaTrash /> Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
