// src/Dashboard/FleetDashboard/FleetDashboard.jsx
import React, { useState } from 'react';
import Sidebar from '../../Component/Layouts/SideBar/Sidebar';
import './FleetDashboard.css';

const truckData = [
  {
    id: 'TRK-001',
    model: 'Volvo FH16',
    location: 'Mumbai Terminal',
    nextService: '15/4/2024',
    capacity: '25,000 L',
    status: 'Active'
  },
  {
    id: 'TRK-002',
    model: 'Scania R Series',
    location: 'Delhi Depot',
    nextService: '10/4/2024',
    capacity: '30,000 L',
    status: 'Maintenance'
  },
  {
    id: 'TRK-003',
    model: 'Mercedes Actros',
    location: 'Bangalore Hub',
    nextService: '20/4/2024',
    capacity: '28,000 L',
    status: 'Active'
  }
];

const FleetDashboard = () => {
  const [search, setSearch] = useState('');
  const [filter, setFilter] = useState('All');

  const filteredTrucks = truckData.filter(truck => {
    const matchesSearch =
      truck.id.toLowerCase().includes(search.toLowerCase()) ||
      truck.model.toLowerCase().includes(search.toLowerCase());
    const matchesStatus = filter === 'All' || truck.status === filter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="fleet-dashboard">
      <Sidebar />
      <div className="fleet-content">
        <h1>Fleet Management</h1>

        <div className="fleet-controls">
          <input
            type="text"
            placeholder="Search by plate or model..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <select value={filter} onChange={e => setFilter(e.target.value)}>
            <option value="All">All Status</option>
            <option value="Active">Active</option>
            <option value="Maintenance">Maintenance</option>
          </select>
        </div>

        <div className="fleet-cards">
          {filteredTrucks.map(truck => (
            <div key={truck.id} className="fleet-card">
              <h3>{truck.id} ({truck.model})</h3>
              <p><strong>Location:</strong> {truck.location}</p>
              <p><strong>Next Service:</strong> {truck.nextService}</p>
              <p><strong>Capacity:</strong> {truck.capacity}</p>
              <p><strong>Status:</strong> <span className={truck.status === 'Active' ? 'active' : 'maintenance'}>{truck.status}</span></p>
              <div className="fleet-actions">
                <button>View Details</button>
                <button>Schedule Service</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FleetDashboard;
