// src/Dashboard/OilManagement/OilManagement.jsx
import React from 'react';
import Sidebar from '../../../Components/SideBar/Sidebar';
import './Oilmanagement.css';

const operations = [
  {
    id: 1,
    dateTime: '25/1/2024, 10:30:00 am',
    type: 'Tanker Fill',
    tanker: '1',
    truck: '-',
    quantity: '40,000',
    location: 'Refinery A',
    status: 'completed'
  },
  {
    id: 2,
    dateTime: '25/1/2024, 2:15:00 pm',
    type: 'Truck Fill',
    tanker: '1',
    truck: '1',
    quantity: '25,000',
    location: 'Mumbai Terminal',
    status: 'completed'
  },
  {
    id: 3,
    dateTime: '25/1/2024, 4:45:00 pm',
    type: 'Truck Fill',
    tanker: '2',
    truck: '3',
    quantity: '28,000',
    location: 'Bangalore Hub',
    status: 'pending'
  }
];

const Oilmanagement = () => {
  const handleEdit = (id) => {
    console.log(`Edit operation ${id}`);
  };

  const handleDelete = (id) => {
    console.log(`Delete operation ${id}`);
  };

  const handleFillTanker = () => {
    console.log('Fill Tanker clicked');
  };

  const handleFillTruck = () => {
    console.log('Fill Truck clicked');
  };

  return (
    <div className="oil-dashboard">
      <Sidebar />
      <div className="oil-content">
        <h1>Oil Management - Operations History</h1>

        <div className="oil-actions">
          <button onClick={handleFillTanker} className="fill-btn">Fill Tanker</button>
          <button onClick={handleFillTruck} className="fill-btn">Fill Truck</button>
        </div>

        <table className="oil-table">
          <thead>
            <tr>
              <th>Date & Time</th>
              <th>Type</th>
              <th>Tanker</th>
              <th>Truck</th>
              <th>Quantity (L)</th>
              <th>Location</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {operations.map(op => (
              <tr key={op.id}>
                <td>{op.dateTime}</td>
                <td>{op.type}</td>
                <td>{op.tanker}</td>
                <td>{op.truck}</td>
                <td>{op.quantity}</td>
                <td>{op.location}</td>
                <td className={op.status === 'completed' ? 'completed' : 'pending'}>{op.status}</td>
                <td>
                  <button onClick={() => handleEdit(op.id)} className="edit-btn">Edit</button>
                  <button onClick={() => handleDelete(op.id)} className="delete-btn">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Oilmanagement;
