import React, { useState } from 'react';
import './AddTruck.css';

const AddTruckForm = ({ onClose, onAdd }) => {
  const [formData, setFormData] = useState({
    plateNumber: '',
    model: '',
    capacity: '',
    location: '',
    status: 'Active',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { plateNumber, model, capacity, location } = formData;

    if (plateNumber && model && capacity && location) {
      onAdd(formData);
      setFormData({
        plateNumber: '',
        model: '',
        capacity: '',
        location: '',
        status: 'Active',
      });
      onClose();
    } else {
      alert('Please fill all fields');
    }
  };

  return (
    <div className="add-truck-form">
      <h2>Add New Truck</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Plate Number
          <input
            type="text"
            name="plateNumber"
            value={formData.plateNumber}
            onChange={handleChange}
          />
        </label>
        <label>
          Model
          <input
            type="text"
            name="model"
            value={formData.model}
            onChange={handleChange}
          />
        </label>
        <label>
          Capacity (Liters)
          <input
            type="text"
            name="capacity"
            value={formData.capacity}
            onChange={handleChange}
          />
        </label>
        <label>
          Location
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
          />
        </label>
        <label>
          Status
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
        </label>
        <div className="form-buttons">
          <button type="button" onClick={onClose}>Cancel</button>
          <button type="submit">Add Truck</button>
        </div>
      </form>
    </div>
  );
};

export default AddTruckForm;
